data {
  int&lt;lower=0&gt; N;
  array[N] int&lt;lower=0, upper=1&gt; y;
}
parameters {
  real&lt;lower=0, upper=1&gt; theta;
}
model {
  for (i in 1:N) {
    target += log(theta * y[i] + 
              log(1 - theta) * (1 - y[i]);
  }
}

